package techmaster;

public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=15; //byte data type can take only 2 digits
		int b=9999; //integer can take more than 4 digits and double also
		short c=9998; //short can take 4 digits 
		
		System.out.println(a); // it will display a value 15,, which is assigned by variable a
		System.out.println("a"); // it will display the a variable because we written in double quotes
		System.out.println(b);
		System.out.println(c);

	}

}
