package com.techmaster.javabasics;

public class Code12121 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		System.out.println(n);
		System.out.println(++n);
		System.out.println(--n);
		System.out.println(++n);
		System.out.println(--n);

	}

}
