package com.techmaster.javabasics;

public class LogicalORexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=53;
		int n2=29;
		int n3=42;
		int n4=28;
		int n5=51;
		
		System.out.println(n1<51 || n2==29);
		System.out.println(n2>27 || n5 != 51);
		System.out.println(n3>43 || n5==51);
		System.out.println(n2!=29 || n4>29);

	}

}
