package com.techmaster.javabasics;

public class Unaryexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=7;
		num++;//7++ post-increment
		++num;//9
		System.out.println(++num);//10
		System.out.println(num++);//10++
		--num;//11-1=10
		System.out.println(--num);//10-1=9

	}

}
